mapper.insert:
insert into role (userName, name, tel, 
  loginTime, establishTime, roleName
  )
values (#{userName,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{tel,jdbcType=VARCHAR},
  #{loginTime,jdbcType=DATE}, #{establishTime,jdbcType=DATE}, #{roleName,jdbcType=VARCHAR}
  )

mapper.select:
select id, userName, name, tel, loginTime, establishTime, roleName
from role
where id = #{id,jdbcType=BIGINT}

select id, userName, name, tel, loginTime, establishTime, roleName
from role
limit #{start},#{rows}
