mapper.update:
update specifics
set beforeSpecific = #{beforeSpecific,jdbcType=VARCHAR},
  stockSpecific = #{stockSpecific,jdbcType=VARCHAR},
  number = #{number,jdbcType=INTEGER},
  price = #{price,jdbcType=DECIMAL}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, beforeSpecific, stockSpecific, number, price
from specifics
where id = #{id,jdbcType=BIGINT}
